mapper.select:
select
${distinct}

    ${resultColumn}




from vehicle_license





        ${order}


        inner join
        (
        SELECT id as primary_key FROM vehicle_license



        ${order}

            LIMIT
                #{startRow,jdbcType=INTEGER},#{rowSize,jdbcType=INTEGER}

            LIMIT #{rowSize,jdbcType=INTEGER}

        ) as tmp
        on tmp.primary_key=vehicle_license.id

select

from vehicle_license

    and vehicle_license.total_mass = #{totalMass,jdbcType=VARCHAR}
    and vehicle_license.curb_weight = #{curbWeight,jdbcType=VARCHAR}
    and vehicle_license.vin = #{vin,jdbcType=VARCHAR}
    and vehicle_license.model = #{model,jdbcType=VARCHAR}
    and vehicle_license.use_character = #{useCharacter,jdbcType=VARCHAR}
    and vehicle_license.document_no = #{documentNo,jdbcType=VARCHAR}
    and vehicle_license.plate_no = #{plateNo,jdbcType=VARCHAR}
    and vehicle_license.issue_date = #{issueDate,jdbcType=DATE}
    and vehicle_license.engine_no = #{engineNo,jdbcType=VARCHAR}
    and vehicle_license.approved_load = #{approvedLoad,jdbcType=VARCHAR}
    and vehicle_license.remark = #{remark,jdbcType=VARCHAR}
    and vehicle_license.register_date = #{registerDate,jdbcType=DATE}
    and vehicle_license.vehicle_type = #{vehicleType,jdbcType=VARCHAR}
    and vehicle_license.inspection_record =
        #{inspectionRecord,jdbcType=VARCHAR}

    and vehicle_license.approved_passengers_capacity =
        #{approvedPassengersCapacity,jdbcType=VARCHAR}

    and vehicle_license.company_id = #{companyId,jdbcType=VARCHAR}
    and vehicle_license.address = #{address,jdbcType=VARCHAR}
    and vehicle_license.traction_mass = #{tractionMass,jdbcType=VARCHAR}
    and vehicle_license.overall_size = #{overallSize,jdbcType=VARCHAR}
    and vehicle_license.owner = #{owner,jdbcType=VARCHAR}
    and vehicle_license.id = #{id,jdbcType=INTEGER}

mapper.update:
update vehicle_license set
owner=#{owner,jdbcType=VARCHAR}
,overall_size=#{overallSize,jdbcType=VARCHAR}
,traction_mass=#{tractionMass,jdbcType=VARCHAR}
,address=#{address,jdbcType=VARCHAR}
,company_id=#{companyId,jdbcType=VARCHAR}
,approved_passengers_capacity=#{approvedPassengersCapacity,jdbcType=VARCHAR}
,inspection_record=#{inspectionRecord,jdbcType=VARCHAR}
,vehicle_type=#{vehicleType,jdbcType=VARCHAR}
,register_date=#{registerDate,jdbcType=DATE}
,remark=#{remark,jdbcType=VARCHAR}
,approved_load=#{approvedLoad,jdbcType=VARCHAR}
,engine_no=#{engineNo,jdbcType=VARCHAR}
,issue_date=#{issueDate,jdbcType=DATE}
,plate_no=#{plateNo,jdbcType=VARCHAR}
,document_no=#{documentNo,jdbcType=VARCHAR}
,use_character=#{useCharacter,jdbcType=VARCHAR}
,model=#{model,jdbcType=VARCHAR}
,vin=#{vin,jdbcType=VARCHAR}
,curb_weight=#{curbWeight,jdbcType=VARCHAR}
,total_mass=#{totalMass,jdbcType=VARCHAR}
where id=#{id,jdbcType=INTEGER}
